mapper.select:

    select i.*, c.companyId from t_incoming_type i,t_company c where i.companyId = c.companyId  and c.companyId = #{companyId} and i.inTypeStatus = 'Y'


  and i.companyId = #{user.companyId}

limit #{beginIndex}, #{pageSize}


  select i.*, c.companyId from t_incoming_type i,t_company c where i.companyId = c.companyId and c.companyId = #{companyId} and i.inTypeStatus = 'N'


  and i.companyId = #{user.companyId}

limit #{beginIndex}, #{pageSize}
