mapper.select:
select tuser.userId, tuser.userName, acc.accId, acc.accName, record.recordId, checkin.checkinId, checkin.carplate, mater.* from (select *,'U' as 'flag' from t_material_use union all select *,'R' as 'flag' from t_material_return) mater left join t_work_info workinfo on mater.matainRecordId= workinfo.recordId left join t_accessories acc on acc.accId = mater.accId left join t_user tuser on workinfo.userId = tuser.userId left join t_maintain_record record on mater.matainRecordId = record.recordId left join t_checkin checkin on record.checkinId = checkin.checkinId
tuser.userId = #{userId}
ORDER BY mater.muUseDate desc limit #{pager.beginIndex}, #{pager.pageSize}

select hipro.*, rutask.ID_ as "taskId" from act_ru_execution ruexe1 LEFT JOIN act_ru_task rutask on ruexe1.PROC_INST_ID_ = rutask.PROC_INST_ID_ left join act_ru_identitylink ruiden1 on ruiden1.TASK_ID_ = rutask.ID_ left join act_hi_procinst hipro on ruexe1.PROC_INST_ID_ = hipro.PROC_INST_ID_ where ruiden1.TYPE_ = 'candidate' and ruexe1.ACT_ID_ = #{curActId} and ruexe1.PROC_INST_ID_ in ( /* -- 最外层, 这层过滤出库管可以审核的流程*/ select ruexe.PROC_INST_ID_ from act_re_procdef reprodep /*-- 第一层嵌套,这层查询出所属公司的申请流程*/ left join act_ru_execution ruexe on reprodep.ID_ = ruexe.PROC_DEF_ID_ left join act_ru_identitylink ruiden on ruiden.PROC_INST_ID_ = ruexe.PROC_INST_ID_ left join act_ru_variable rvar on ruexe.PROC_INST_ID_ = rvar.PROC_INST_ID_ where reprodep.KEY_ = #{flowName} and ruiden.TYPE_ = "starter" and ruiden.USER_ID_ in ( select userId from t_user where companyId = #{companyId} /*-- 第二层嵌套, 这层查询出所属公司的员工ID,流程发起人是公司的员工*/ ) and rvar.name_ = 'accCount'
and rvar.text_ > 0
and rvar.text_ < 0
) order by hipro.start_time_ desc limit #{pager.beginIndex}, #{pager.pageSize}

select count(DISTINCT ruexe.PROC_INST_ID_) from act_re_procdef repro
left join act_ru_execution ruexe on repro.ID_ =  ruexe.PROC_DEF_ID_
left join act_hi_procinst hipro on ruexe.PROC_INST_ID_ = hipro.PROC_INST_ID_
where repro.KEY_ = #{flowName}
and hipro.START_USER_ID_ = #{userId};

select count(record.recordId) from t_checkin checkin left JOIN t_maintain_record record on checkin.checkinId = record.checkinId where record.recordStatus='Y'
and checkin.companyId = #{companyId}
and record.recordId not in (select workinfo.recordId from t_work_info workinfo)

/* 后期可能会加上员工角色 */
     select t_user.userId , t_user.userName from t_user left join t_company on t_company.companyId = t_user.companyId
     where t_company.companyId = #{companyId};

select workinfo.workId 'workId', record.recordId 'recordId', color.colorRGB 'colorRGB', color.colorName 'colorName', checkin.carplate 'carplate', model.modelName 'carmodel', brand.brandName 'carbrand', checkin.userRequests 'useRequests', workinfo.workAssignTime 'workAssignTime' from t_work_info workinfo left JOIN t_maintain_record record on workinfo.recordId = record.recordId left JOIN t_checkin checkin on record.checkinId = checkin.checkinId left join t_car_model model on checkin.modelId = model.modelId left join t_car_color color on checkin.colorId = color.colorId left join t_car_brand brand on checkin.brandId = brand.brandId
workinfo.userId = #{userId}
and workinfo.workStatus = #{status}
limit #{pager.beginIndex}, #{pager.pageSize}

select
count(workinfo.workId)
from
t_work_info workinfo

    workinfo.userId = #{userId}

        and workinfo.workStatus = #{status}

select record.recordId, detail.maintainDetailId,detail.maintainDiscount,fix.* from t_maintain_record record
left join t_maintain_detail detail on record.recordId = detail.maintainRecordId
left join t_maintain_fix fix on detail.maintainItemId = fix.maintainId
where record.recordId = #{recordId} and fix.companyId = #{companyId}
limit #{pager.beginIndex}, #{pager.pageSize}
