mapper.select:
SELECT t.*,u.userId AS user_id,u.userName AS user_name,
u2.userId AS admin_id,u2.userName AS admin_name FROM t_track_list t
LEFT JOIN t_user u ON t.trackUser = u.userId
LEFT  JOIN t_user u2 ON t.userId = u2.userId

  and t.companyId = #{user.companyId}

limit #{beginIndex}, #{pageSize}
